import { PlusOutlined } from '@ant-design/icons';
import PropTypes from 'prop-types';
import React, { useCallback, useState } from 'react';

import ImageZoom from './imagesZoom/index';

const PostImages = ({images}) => {
    const [showImagesZoom,setShowImagesZoom] = useState(false);

    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, []);
    const onClose = useCallback(() => {
        setShowImagesZoom(false);
    }, []);

    if(images.length === 1){
        return (
            <>
                <img role="presentation" src={images[0].src} alt={images[0].src} onClick={onZoom} />
                {showImagesZoom && <ImageZoom image={images} onClose={onClose} />}
            </>
        );
    }
    if(images.length === 2){
        return (
            <>
                <img role="presentation" style={{width:'50%', display:'inline-block'}} 
                src={images[0].src} alt={images[0].src} onClick={onZoom} />
                <img role="presentation" style={{width:'50%', display:'inline-block'}} 
                src={images[1].src} alt={images[1].src} onClick={onZoom} />
                {showImagesZoom && <ImageZoom image={images} onClose={onClose} />}
            </>
        );
    }
    return (
        <>
            <div>
            <img role="presentation" style={{width:'50%'}} src={images[0].src} alt={images[0].src} onClick={onZoom} />
            <div role="presentation"
                style={{width:'50%', display: 'inline-block', textAlign:'center', verticalAlign:'middle' }}
                onClick={onZoom}>
                    <PlusOutlined />
                    <br />
                    {images.length - 1} 개의 사진 더보기
                </div>
            </div>
            {showImagesZoom && <ImageZoom image={images} onClose={onClose} />}
        </>
    );
};

PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
}

export default PostImages;
