import React, { useCallback, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import {Button, Card, Popover, Avatar, List, Comment} from 'antd';
import {RetweetOutlined, HeartOutlined, HeartTwoTone,
    MessageOutlined, EllipsisOutlined} from '@ant-design/icons';
import { useSelector, useDispatch } from 'react-redux';
import Link from 'next/link';
import moment from 'moment';

import PostImages from './PostImages';
import CommentForm from './CommentForm';
import PostCardContent from './PostCardContent';
import { REMOVE_POST_REQUEST, LIKE_POST_REQUEST,
     UNLIKE_POST_REQUEST, RETWEET_REQUEST } from '../reducers/post';
import FollowButton from './FollowButton';

moment.locale('ko') //한글로 변환

const PostCard = ({post}) => {
    const dispatch = useDispatch();
    const {removePostLoading} = useSelector((state) => state.post);
    const [commentFormOpened, setCommentFormOpened] = useState(false);

    const {me} = useSelector((state) => state.user);
    const id = me?.id;  //me.id가 있으면 id가 생성됨
    const liked = post.Likers.find((v) => v.id === id);

    const onLike = useCallback(() => {
        if(!id){
            return alert('로그인이 필요합니다.');
        }
        return dispatch({
            type: LIKE_POST_REQUEST,
            data: post.id,
        });
    }, []);
    const onUnLike = useCallback(() => {
        if(!id){
            return alert('로그인이 필요합니다.');
        }
        return dispatch({
            type: UNLIKE_POST_REQUEST,
            data: post.id,
        });
    }, []);
    const onToggleComment = useCallback(() => {
        setCommentFormOpened((prev) => !prev);
    }, []);
    const onRemovePost = useCallback(() => {
        if(!id){
            return alert('로그인하지 않고 게시글을 삭제할 수 없습니다.');
        }
        return dispatch({
            type: REMOVE_POST_REQUEST,
            data: post.id,
        });
    }, []);
    const onRetweet = useCallback(() => {
        if(!id){
            return alert('로그인하지 않고 리트윗할 수 없습니다.');
        }
        return dispatch({
            type: RETWEET_REQUEST,
            data: post.id,
        });
    });

    return(
        <div>
            <Card 
            cover={post.Images[0] && <PostImages images={post.Images} />}
            actions={[
                <RetweetOutlined key="retweet" onClick={onRetweet}/>,
                liked 
                    ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onUnLike} />
                    :<HeartOutlined key="heart" onClick={onLike} />,
                <MessageOutlined key="comment" onClick={onToggleComment} />,
                <Popover key="more" content={(
                    <Button.Group>
                        {id && post.User.id === id ? (
                        <>
                            <Button>수정</Button>
                            <Button type="danger" onClick={onRemovePost} loading={removePostLoading}>
                                삭제
                            </Button>
                        </>
                        ) : <Button>신고</Button>}
                    </Button.Group>
                )}>
                    <EllipsisOutlined />
                </Popover>,
            ]}
            title={post.RetweetId ? `${post.User.nickname}님이 리트윗 하셨습니다.` : null}
            extra={id && <FollowButton post={post} />}
            >
                {post.RetweetId && post.Retweet 
                ? (
                    <Card
                    cover={post.Retweet.Images[0] && <PostImages images={post.Retweet.Images} />} >
                    <div style={{float: 'right'}}>{moment(post.createdAt).format('YYYY.MM.DD')}</div>
                    <Card.Meta avatar={(
                        <Link href={`/user/${post.Retweet.User.id}`}>
                            <a><Avatar>{post.Retweet.User.nickname[0]}</Avatar></a>
                        </Link>
                    )}
                    title={post.Retweet.User.nickname} description={<PostCardContent postData={post.Retweet.content} />} />
                    </Card>
                )
                : (
                    <>
                        <div style={{float: 'right'}}>{moment(post.createdAt).format('YYYY.MM.DD')}</div>
                        <Card.Meta avatar={(
                            <Link href={`/user/${post.User.id}`}>
                                <a><Avatar>{post.User.nickname[0]}</Avatar></a>
                            </Link>
                        )}
                        title={post.User.nickname} description={<PostCardContent postData={post.content} />} />
                    </>
                )}

            </Card>
            {commentFormOpened && (
                <div>
                    <CommentForm post={post} />
                    <List
                        header ={`${post.Comments.length} 개의 댓글`}
                        itemLayout="horizontal"
                        dataSource={post.Comments}
                        renderItem={(item) => (
                            <li>
                                <Comment
                                    author={item.User.nickname}
                                    avatar={(
                                        <Link href={`/user/${item.User.id}`}>
                                            <a><Avatar>{item.User.nickname[0]}</Avatar></a>
                                        </Link>                                        
                                    )}
                                    content={item.content}
                                />
                            </li>
                        )}
                    />
                </div>
            )}
        </div>
    );
}

PostCard.propTypes = {
    post:PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.string,
        Comments: PropTypes.arrayOf(PropTypes.object),
        Images: PropTypes.arrayOf(PropTypes.object),
        Likers: PropTypes.arrayOf(PropTypes.object),
        RetweetId: PropTypes.number,
        Retweet: PropTypes.objectOf(PropTypes.any),
    }).isRequired,
}

export default PostCard;
